<template><form @submit.prevent="onSubmit"><h1>Login</h1><input v-model="u" placeholder="user"/><input v-model="p" type="password" placeholder="pass"/><button>Sign in</button></form></template><script setup lang="ts">import { ref } from 'vue'; import { useRouter } from 'vue-router'; import { useAuth } from '../stores/auth'; const u=ref(''); const p=ref(''); const r=useRouter(); const a=useAuth(); const onSubmit=()=>{ if(u.value&&p.value){ a.token='demo'; r.push('/'); } };</script>
